import React from "react";
import "./mymodal.css";

const addBoardBtnolors = ["primary", "secondary", "success", "info", "warning", "danger", "light"]

const MyModal = (props) => {
  return (
    <div className={props.activeModal ? "mymodal " : "mymodal close"} onClick={() => props.setActiveModal(false)}>
      <div className="mymodal-body" onClick={(e) => e.stopPropagation()}>


        <button type="button" className="btn btn-danger btnModalClose" onClick={() => props.setActiveModal(false)}>x</button>
        <div>
          {addBoardBtnolors.map(item =>
            <button type="button" className={`btn btn-${item}`}>{item.charAt(0).toUpperCase() + item.slice(1)}
            </button>)}
        </div>
        <button type="button" className="btn btn-danger " onClick={() => props.setActiveModal(false)}>Cancel</button>
      </div>
    </div >
  )
}

export default MyModal
