import React, { useState } from 'react'
import Board from './Board'
import './AddMyTrelloStyle.css'
// import '../bootstrap.min.css'
import 'bootswatch/dist/darkly/bootstrap.min.css';
import Nav from './Nav'

const AddMyTrello = () => {
    const [boards, setBoards] = useState([
        { id: 1, title: "Сделать", items: [{ id: 1, title: "Пойти в магазин" }, { id: 2, title: "Пойти в спортзал" }, { id: 3, title: "Покормить кота" }] },
        { id: 2, title: "Проверить", items: [{ id: 4, title: "Проверить уроки" }, { id: 5, title: "Задачи по математике" }, { id: 6, title: "Коммунальные платежи" }] },
        { id: 3, title: "Сделано", items: [{ id: 7, title: "Поменять резину" }, { id: 8, title: "Приготовить ужин" }, { id: 9, title: "Постирать вещи" }] },

    ])

    var obj2 = {
        store: 'Walmart',
    };


    // let x = [...boards]
    // x.push({ title: '00000' })
    // console.log(x);

    // changeBoards(x, boards)

    // console.log(boards);

    const [currentBoard, setCurrentBoard] = useState(null)
    const [cureentItem, setCureentItem] = useState(null)

    function changeBoards(newBoard, oldBoardS) {
        setBoards([...oldBoardS, newBoard])
    }

    return (
        <div>
            <div>
                {/* <!-- Button trigger modal --> */}
                <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
                    Запустить модальное окно
                </button>

                {/* <!-- Modal --> */}
                <div className="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                            <div className="modal-body">
                                ...
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                <button type="button" className="btn btn-primary">Save changes</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>


            <Nav boards={boards} changeBoards={changeBoards} setBoards={setBoards}></Nav>
            <div className='appTrello'>

                {boards.map((board, index) =>
                    <Board
                        key={index + 'Board'}
                        board={board}
                        index={index}
                        currentBoard={currentBoard}
                        setCurrentBoard={setCurrentBoard}
                        cureentItem={cureentItem}
                        setCureentItem={setCureentItem}
                        boards={boards}
                        setBoards={setBoards}
                    >
                    </Board>)}
            </div>

        </div>

    )
}

export default AddMyTrello


