import React, { useEffect, useState } from 'react'
import MyModal from './Modal/MyModal'

const Nav = (props) => {

    const [activeModal, setActiveModal] = useState(false)
    const [newNameBoard, setNewNameBoard] = useState(null)
    console.log(props.newColorOfBoard);

    const openModalAddBoardBtn = () => {
        setActiveModal(true)
        // let newTitle = prompt("Название доски ?")
        // let newItems = prompt("Внесите дела на доску, через запятую")
        // newItems?.split(',')
        // console.log(newItems);
        // let container = []
        // newItems.forEach(item => container.push({ id: Math.random(), 'title': item }))
        // newItems.map(item => [{ title: item }])
        // let r = newItems?.map(item => [{ title: item }])
        // console.log(r);

        // let newBoard = { id: Date.now(), title: newTitle, items: [] }
        // console.log(newBoard);
        // props.changeBoards(newBoard, props.boards)
    }

    function addBoard() {
        // console.log(props.newColorOfBoard);
        // let newBoard = { id: Date.now(), title: newNameBoard, items: [{ id: 777, title: "Внеси задачу!!" }], background: props.newColorOfBoard }
        // console.log(newBoard);
        // props.changeBoards(newBoard, props.boards)
        // changeBoards(newBoard, props.boards)
        // setActiveModal(false)

    }

    function greatNewBoard(e, item) {
        console.log(item);
        props.setNewColorOfBoard(item)
        let newBoard = { id: Date.now(), title: newNameBoard, items: [{ id: 777, title: "Внеси задачу!!" }], background: item }
        changeBoards(newBoard, props.boards)
        setActiveModal(false)

        addBoard()
    }


    function changeBoards(newBoard, oldBoardS) {
        console.log(newBoard);
        props.setBoards([...oldBoardS, newBoard])
    }




    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
                <div className="container-fluid">
                    <a className="navbar-brand" href="#">MyTrello</a>

                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarColor01" aria-controls="navbarColor01" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>

                    {/* <button onClick={addBord} type="button" className="btn btn-primary">Primary</button> */}

                    <div className="collapse navbar-collapse" id="navbarColor01">
                        <ul className="navbar-nav me-auto">
                            <li className="nav-item">
                                <a className="nav-link" href="#" onClick={openModalAddBoardBtn}>ADD Board</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">EMPTY</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">About</a>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav >
            <MyModal activeModal={activeModal} setActiveModal={setActiveModal} addBoard={addBoard}
                newNameBoard={newNameBoard} setNewNameBoard={setNewNameBoard}
                newColorOfBoard={props.newColorOfBoard} setNewColorOfBoard={props.setNewColorOfBoard}
                greatNewBoard={greatNewBoard}

            ></MyModal>
        </div >

    )
}

export default Nav
