import React, { useState } from 'react'

const DragDrop = () => {
    const [cardsList, setCardsList] = useState([
        { id: 1, order: 3, text: 'Card 3' },
        { id: 2, order: 2, text: 'Card 2' },
        { id: 3, order: 1, text: 'Card 1' },
        { id: 3, order: 4, text: 'Card 4' },
    ])
    return (
        <div>
            {cardsList.map(card => {
                <div>
                    {cardsList.text}
                </div>
            })}
        </div>
    )
}

export default DragDrop
