import Modal from './Modal/MyModal'
import React, { useState } from 'react'
import MyModal from './Modal/MyModal'

const Nav = (props) => {

    const addBord = () => {
        setActiveModal(false)
        let newTitle = prompt("Название доски ?")
        // let newItems = prompt("Внесите дела на доску, через запятую")
        // newItems?.split(',')
        // console.log(newItems);
        // let container = []
        // newItems.forEach(item => container.push({ id: Math.random(), 'title': item }))
        // newItems.map(item => [{ title: item }])
        // let r = newItems?.map(item => [{ title: item }])
        // console.log(r);

        let newBoard = { id: Date.now(), title: newTitle, items: [] }
        console.log(newBoard);
        props.changeBoards(newBoard, props.boards)


    }

    const [activeModal, setActiveModal] = useState(false)

    return (
        <div>


            <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
                <div className="container-fluid">
                    <a className="navbar-brand" href="#">MyTrello</a>

                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarColor01" aria-controls="navbarColor01" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>

                    {/* <button onClick={addBord} type="button" className="btn btn-primary">Primary</button> */}

                    <div className="collapse navbar-collapse" id="navbarColor01">
                        <ul className="navbar-nav me-auto">
                            <li className="nav-item">
                                <a className="nav-link" href="#" onClick={addBord}>ADD Board</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">EMPTY</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">About</a>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav >
            <MyModal activeModal={activeModal} setActiveModal={setActiveModal} ></MyModal>
        </div >

    )
}

export default Nav
