import React, { useState } from "react";
import "./adModalTodo.css";




const MyModal = (props) => {

  const [addTodoModal, setAddTodoModal] = useState(true)

  function handlerAddTodo() {
    console.log('todo');
    setAddTodoModal(false)
  }




  return (
    <div className={addTodoModal ? "mymodal " : "mymodal close"} onClick={() => setAddTodoModal(false)}>

      {/* остановить всплытие события выше этого блока */}
      <div className="mymodal-body" onClick={(e) => e.stopPropagation()}>

        <button type="button" className="btn btn-danger btnModalClose" onClick={() => setAddTodoModal(false)}>x</button>
        <div>

          <input type="text" className="form-control" placeholder="Введите название доски"
            id="inputDefault" onFocus={(event) => (event.target.placeholder = "")}
            onChange={(event) => { console.log(event) }}
          ></input>

        </div>

        <button type="button" className="btn btn-danger "
          onClick={handlerAddTodo}>Cancel</button>
      </div>

    </div >
  )
}

export default MyModal
