import React, { useState } from 'react'
import Board from './Board'
import './AddMyTrelloStyle.css'
// import '../bootstrap.min.css'
import 'bootswatch/dist/darkly/bootstrap.min.css';
import Nav from './Nav'

const AddMyTrello = () => {
    const [boards, setBoards] = useState([
        { id: 1, title: "Сделать", background: 'success', items: [{ id: 1, title: "Пойти в магазин" }, { id: 2, title: "Пойти в спортзал" }, { id: 3, title: "Покормить кота" }] },
        { id: 2, title: "Проверить", background: 'success', items: [{ id: 4, title: "Проверить уроки" }, { id: 5, title: "Задачи по математике" }, { id: 6, title: "Коммунальные платежи" }] },
        { id: 3, title: "Сделано", background: 'success', items: [{ id: 7, title: "Поменять резину" }, { id: 8, title: "Приготовить ужин" }, { id: 9, title: "Постирать вещи" }] },

    ])

    const [newColorOfBoard, setNewColorOfBoard] = useState(null)
    console.log(newColorOfBoard);

    const [currentBoard, setCurrentBoard] = useState(null)
    const [cureentItem, setCureentItem] = useState(null)


    // function changeBoards(newBoard, oldBoardS) {

    //     console.log(newBoard);
    //     setBoards([...oldBoardS, newBoard])
    // }



    return (
        <div>
            {/* changeBoards={changeBoards} */}
            <Nav boards={boards} setBoards={setBoards}
                newColorOfBoard={newColorOfBoard} setNewColorOfBoard={setNewColorOfBoard}
            ></Nav>
            <div className='appTrello'>

                {boards.map((board, index) =>
                    <Board
                        key={index + 'Board'}
                        board={board}
                        index={index}
                        currentBoard={currentBoard}
                        setCurrentBoard={setCurrentBoard}
                        cureentItem={cureentItem}
                        setCureentItem={setCureentItem}
                        boards={boards}
                        setBoards={setBoards}
                        newColorOfBoard={newColorOfBoard}
                    >
                    </Board>)}
            </div>

        </div>

    )
}

export default AddMyTrello


