import React from 'react'
import Item from './Item'

const Board = (props) => {

    // console.log('Board', props);

    let board = props.board
    let currentBoard = props.currentBoard
    // let setCurrentBoard = props.setCurrentBoard

    let cureentItem = props.cureentItem
    let boards = props.boards
    let setBoards = props.setBoards

    function dropCardHandler(e, board) {

        board.items.push(cureentItem)

        const currentIndex = currentBoard.items.indexOf(cureentItem)
        // console.log('board', board, 'cureentItem', cureentItem, 'currentBoard', currentBoard);
        // console.log(currentIndex);
        currentBoard.items.splice(currentIndex, 1)
        setBoards(boards.map(b => {
            if (b.id === board.id) {
                return board
            }
            if (b.id === currentBoard.id) {
                return currentBoard
            }
            return b
        }
        ))

        e.target.style.boxShadow = 'none'
    }

    function dragOverHandler(e) {
        e.preventDefault()
        if (e.target.className === "item") {
            e.target.style.boxShadow = '0 5px 3px #673ab7 '
        }
    }

    return (
        <div
            className='board'
            onDragOver={(e) => { dragOverHandler(e) }}
            onDrop={(e) => { dropCardHandler(e, board) }}
        >
            <div className="board_title">{board.title}</div>
            {<Item
                props={props}
            />}




            <div className="card text-white bg-success mb-3" style={{ maxWidth: '20rem' }}>
                <div className="card-header">{board.title}</div>
                <div className="card-body">
                    <h4 className="card-title">Success card title</h4>
                    <p className="card-text">Some quick example
                        text to build on the card title and make up the bulk of the card's content.</p>
                    <div className="alert alert-dismissible alert-success">
                        <button type="button" className="btn-close" data-bs-dismiss="alert"></button>
                        You successfully read <a href="#" className="alert-link">this important alert message</a>.
                    </div>
                </div>
            </div>
        </div >
    )
}

export default Board
